import React from 'react';
import classes from './Home.module.css';
//import Projects from '../../Components/Projects/Projects';
import Project from '../../Components/Projects/Project/Project';
import picOne from '../../assets/images/c#logo.png';

class Home extends React.Component {

    ecommerceClickHandler = () => {
        this.props.history.push('/ecommerce');
    }
    wineryClickHandler = () => {
        this.props.history.push('/winerylandingpage');
    }
    weatherClickHandler = () => {
        //console.log(this.props);
        this.props.history.push('/weatherfinder');
    }
    resumeClickHandler = () => {
        this.props.history.push('/resume');
    }

    render() {
        //let attachedClasses = [classes.Home, classes.overlay];

        return (

            <div className={classes.Home}>
                <div className={classes.overlay}>
                    <h1>Alex Forest</h1>
                    <h4>Software Developer</h4>
                    <div className={classes.ProjectDiv}>
                        <h4>Projects</h4>
                        {/*<Projects projectClick={this.projectClickHandler}/>*/}
                        <div>
                            <Project title="E-Commerce Site" desc="ASP.NET C# Bootstrap CSS" click={this.ecommerceClickHandler} pic={picOne}/>
                            <Project title="Winery Landing Page" desc="CSS Grid, Flexbox, Media Queries" click={this.wineryClickHandler}/>
                            <Project title="Weather Finder" desc="Vanilla JavaScript, AJAX" click={this.weatherClickHandler}/>
                        </div>

                        <button onClick={this.resumeClickHandler}>RESUME</button>
                    </div>
                </div>

            </div>
        )
    }
}

export default Home;
