import React from 'react';
import classes from './Ecommerce.module.css';
import image from '../../../assets/images/winter2.jpg';

class Ecommerce extends React.Component {
    render() {
        return (
            <div className={classes.Ecommerce}>
                <h1>E-commerce Site</h1>
                <div className={classes.Content}>
                    <div className={classes.TopRow}>
                        <img src={image} alt="pic"/>
                        <div className={classes.Skills}>
                            <p>SKILLS USED: </p>
                            <div></div>
                            <ul>
                                <li>C#</li>
                                <li>ASP.NET MVC</li>
                                <li>HTML/CSS</li>
                                <li>Bootstrap</li>
                                <li>mySQL</li>
                            </ul>
                        </div>
                    </div>
                    <p>This app is a built-from-the-ground-up, full-fledged E-commerce site. Several different languages were used in the creation of this app: C# for logic and back-end work, SQL for database management, and HTML, CSS, and BootStrap for front-end presentation. This app also uses ASP.NET and the Model-View-Controller (MVC) architecture to create a full-stack application. </p>
                    <p>The app allows users to create their own account, browse/select products from a database of over 20 items, modify their shopping cart, set up billing information, purchase orders, check completed orders, and modify their account information.</p>
                    <button>DEMO</button>
                    <button>LIVE VERSION</button>
                    <button>VIEW CODE</button>
                </div>

            </div>
        );
    }
}

export default Ecommerce;
