import React from 'react';
import classes from '../Ecommerce/Ecommerce.module.css';
import image from '../../../assets/images/winter2.jpg';

class WeatherFinder extends React.Component {
    state = {
        modalOpen: false
    }

    toggleModalHandler() {
        this.setState({modalOpen: !this.state.modalOpen})
    }

    render() {
        let modal;
        if (this.state.modalOpen) {
            modal = <Modal name="Weather Finder" desc="JavaScript, AJAX, HTML, CSS" click={this.toggleModalHandler.bind(this)}/>
        } else {
            modal = "";
        }

        return (
            <div className={classes.Main}>
                <h1>Weather Finder</h1>
                <div className={classes.Content}>
                    <div className={classes.TopRow}>
                        <img src={image} alt="pic"/>
                        <div className={classes.Skills}>
                            <p>SKILLS USED: </p>
                            <div></div>
                            <ul>
                                <li>Vanilla JavaScript</li>
                                <li>AJAX</li>
                                <li>HTML</li>
                                <li>CSS</li>
                            </ul>
                        </div>
                    </div>
                    <div className={classes.Bar}></div>
                    <p>This app allows users to find the current weather data for any city in the world, via search bar. The city typed in by the user then displays on an image of the world, converting the latitude nad longitude of the city to pixels. The map also allows users to click on any of the default cities loaded on the map.</p>
                    <p>This app uses vanilla JavaScript and AJAX to render the weather data, the map, and connect to the DarkSky.net API. The app also uses imported SkyCons animations to display the current weather of any city.</p>
                    <button onClick={this.toggleModalHandler.bind(this)}>DEMO</button>
                    <button>LIVE VERSION</button>
                    <button>VIEW CODE</button>
                </div>

            </div>
        );
    }
}

export default WeatherFinder;
